static DATA: &str = "";

static COUNT: usize = 4;

fn all_equal(chars: Vec<char>) -> bool {
    for a in 0..(COUNT-1) {
        for b in (a+1)..COUNT {
            if chars[a] == chars[b] {
                return false;
            }
        }
    }

    true
}

fn main() {
    let data = DATA.chars().collect::<Vec<char>>();
    for i in 0..data.len() {
        
        if all_equal(data[i..(i+COUNT)].to_vec()) {
            println!("{}", i+COUNT);
            println!("{:?}", data[i..(i+4)].to_vec());
            break;
        }
    }
}
